import React from 'react';
import Plotly from 'react-plotly.js';

class Searchbar extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            ticker: 'RELIANCE.NS',
            data: [],
            layout: {
                title: 'Real-Time Candlestick Chart',
                xaxis: {
                    title: 'Date'
                },
                yaxis: {
                    title: 'Price'
                }
            }
        };
    }

    componentDidMount() {
        this.fetchData();
        setInterval(this.fetchData, 60000);
    }

    fetchData = () => {
        // Use the `fetch` function to retrieve real-time data for the given stock ticker
        const api_key='N3P0WGL6IDTJQUN4';
        let ticker='RELIANCE.NS';
        fetch('https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${ticker}&interval=5min&apikey=${api_key}')
            .then(response => response.json())
            .then(data => {
                // Parse the data and update the state to trigger a re-render
                const parsedData = {
                    x: data.dates,
                    open: data.openPrices,
                    high: data.highPrices,
                    low: data.lowPrices,
                    close: data.closePrices,
                    type: 'candlestick',
                    name: 'Candlestick'
                };

                this.setState({ data: [parsedData] });
            })
            .catch(error => console.error(error));
    }

    handleTickerChange = event => {
        this.setState({ ticker: event.target.value });
    }

    render() {
        return (
            <div>
                <input type="text" placeholder="Enter stock ticker" onChange={this.handleTickerChange} />
                <Plotly
                    data={this.state.data}
                    layout={this.state.layout}
                />
            </div>
        );
    }
}

export default Searchbar;
