import React, {useEffect,useState} from "react";
// import yfinance from "yfinance";
import NavBar from "./NavBar";
import {Routes, Route} from "react-router-dom";
import axios from 'axios';
import "../styles/Dashboard.css";
import yahooFinance from "yahoo-finance";

const STOCK_API_KEY = 'N3P0WGL6IDTJQUN4';
const STOCK_API_URL = "https://www.alphavantage.co/query?function=BATCH_STOCK_QUOTES&symbols=RELIANCE.NSE, HDFCBANK.NSE, INFY.NSE, TCS.NSE, ICICIBANK.NSE, KOTAKBANK.NSE, HINDUNILVR.NSE, BHARTIARTL.NSE, AXISBANK.NSE, ASIANPAINT.NSE&apikey=$N3P0WGL6IDTJQUN4";

function Dashboard () {

    const [stocks, setStocks] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const symbols = ["RELIANCE.NS", "TCS.NS", "HDFCBANK.NS", "HINDUNILVR.NS", "INFY.NS", "KOTAKBANK.NS", "BAJFINANCE.NS", "ICICIBANK.NS", "AXISBANK.NS", "ITC.NS"];
            const stockData = await yahooFinance.quote({ symbols });
            const formattedData = Object.keys(stockData).map((symbol) => ({
                symbol,
                price: stockData[symbol].regularMarketPrice.toFixed(2),
            }));
            setStocks(formattedData);
        };
        fetchData();
        const interval = setInterval(fetchData, 5000); // fetch data every 5 seconds
        return () => clearInterval(interval);
    }, []);

    return(
        <>
            <div className="Navbar-dashboard">
                <NavBar/>
            </div>
            <div className="stock-display">
                <h1>Top 10 Indian Stocks</h1>
                <table>
                    <thead>
                    <tr>
                        <th>Symbol</th>
                        <th>Price (INR)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock, index) => (
                        <tr key={index}>
                            <td>{stock.symbol}</td>
                            <td>{stock.price}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </>


    );
}

export default Dashboard;
