import React, {useEffect,useState} from "react";
// import yfinance from "yfinance";
import NavBar from "./NavBar";
import {Routes, Route} from "react-router-dom";
import axios from 'axios';
import "../styles/Dashboard.css";

const STOCK_API_KEY = 'N3P0WGL6IDTJQUN4';
const STOCK_API_URL = "https://www.alphavantage.co/query?function=TIME_SERIES_WEEKLY&symbol=RELIANCE.NS&apikey=N3P0WGL6IDTJQUN4"

function Dashboard () {

    const [stocks, setStocks] = useState([]);

    useEffect(() => {
        iex.settings.apiToken = "YOUR_API_KEY";
        const fetchData = async () => {
            const symbols = ["RELIANCE", "TCS", "HDFCBANK", "HINDUNILVR", "INFY", "KOTAKBANK", "BAJFINANCE", "ICICIBANK", "AXISBANK", "ITC"];
            const stockData = await iex.batch(symbols, { types: ["quote"] });
            const formattedData = Object.values(stockData).map((quote) => ({
                symbol: quote.symbol,
                price: quote.latestPrice.toFixed(2),
            }));
            setStocks(formattedData);
        };
        fetchData();
        const interval = setInterval(fetchData, 5000); // fetch data every 5 seconds
        return () => clearInterval(interval);
    }, []);

    return(
        <>
            <div className="Navbar-dashboard">
                <NavBar/>
            </div>
            <div className="stock-display">
                <h1>Top 10 Indian Stocks</h1>
                <table>
                    <thead>
                    <tr>
                        <th>Symbol</th>
                        <th>Price (USD)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock, index) => (
                        <tr key={index}>
                            <td>{stock.symbol}</td>
                            <td>{stock.price}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </>


    );
}

export default Dashboard;
