import React, {useEffect,useState} from "react";
// import yfinance from "yfinance";
import NavBar from "./NavBar";
import {Routes, Route} from "react-router-dom";
import axios from 'axios';
import "../styles/Dashboard.css";

// const STOCK_API_KEY = 'N3P0WGL6IDTJQUN4';
// const STOCK_API_URL = "https://www.alphavantage.co/query?function=TIME_SERIES_WEEKLY&symbol=RELIANCE.NS&apikey=N3P0WGL6IDTJQUN4"

function Dashboard () {

    const [stocks, setStocks] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const symbols = ["RELIANCE", "TCS", "HDFCBANK", "HINDUNILVR", "INFY", "KOTAKBANK", "BAJFINANCE", "ICICIBANK", "AXISBANK", "ITC"];
            const response = await fetch(`https://www.nseindia.com/live_market/dynaContent/live_watch/get_quote/GetQuote.jsp?symbol=${symbols.join(",")}`, {
                headers: {
                    "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.36",
                },
            });
            const text = await response.json();
            console.log(text)
            const stockData = symbols.map((symbol) => {
                const regex = new RegExp(`"${symbol}[^}]*?closePrice":"(.*?)"`);
                const match = text.match(regex);
                return { symbol, price: match ? match[1] : "N/A" };
            });
            setStocks(stockData);
        };
        fetchData();
        const interval = setInterval(fetchData, 5000); // fetch data every 5 seconds
        return () => clearInterval(interval);
    }, []);

    return(
        <>
            <div className="Navbar-dashboard">
                <NavBar/>
            </div>
            <div className="stock-display">
                <h1>Top 10 Indian Stocks</h1>
                <table>
                    <thead>
                    <tr>
                        <th>Symbol</th>
                        <th>Price (INR)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock, index) => (
                        <tr key={index}>
                            <td>{stock.symbol}</td>
                            <td>{stock.price}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </>


    );
}

export default Dashboard;
