import React, { useState, useEffect } from 'react';
import Plot from 'react-plotly.js';
import yfinance from 'yfinance';

function Searchbar() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const interval = setInterval(() => {
            yfinance
                .download('AAPL', {
                    period: '1d',
                    interval: '1m',
                })
                .then(data => {
                    setData(data);
                });
        }, 60000);

        return () => clearInterval(interval);
    }, []);

    return (
        <div>
            <Plot
                data={[
                    {
                        type: 'candlestick',
                        x: data.index,
                        open: data.Open,
                        high: data.High,
                        low: data.Low,
                        close: data.Close,
                    },
                ]}
                layout={{
                    width: 800,
                    height: 600,
                    title: 'Real-time Candlestick Graph',
                }}
            />
        </div>
    );
}

export default Searchbar;
