import React, {useEffect,useState} from "react";
// import yfinance from "yfinance";
import NavBar from "./NavBar";
import {Routes, Route} from "react-router-dom";
import axios from 'axios';
import "../styles/Dashboard.css";

const STOCK_API_KEY = 'N3P0WGL6IDTJQUN4';
const STOCK_API_URL = "https://www.alphavantage.co/query?function=BATCH_STOCK_QUOTES&symbols=RELIANCE.NSE, HDFCBANK.NSE, INFY.NSE, TCS.NSE, ICICIBANK.NSE, KOTAKBANK.NSE, HINDUNILVR.NSE, BHARTIARTL.NSE, AXISBANK.NSE, ASIANPAINT.NSE&apikey=$N3P0WGL6IDTJQUN4";

function Dashboard () {

    const [stocks, setStocks] = useState([]);

    useEffect(() => {
        const fetchStocks = async () => {
            try {
                const response = await axios.get(STOCK_API_URL);
                const stockData = response.data['Stock Quotes'].map((stock) => ({
                    symbol: stock['1. symbol'],
                    price: parseFloat(stock['2. price']).toFixed(2),
                }));
                setStocks(stockData);
            } catch (error) {
                console.log(error);
            }
        };

        fetchStocks();
        const interval = setInterval(() => {
            fetchStocks();
        }, 5000);
        return () => clearInterval(interval);
    }, []);


    return(
        <>
            <div className="Navbar-dashboard">
                <NavBar/>
            </div>
            <div className="stock-display">
                <h1>Top 10 Indian Stocks</h1>
                <table>
                    <thead>
                    <tr>
                        <th>Symbol</th>
                        <th>Price</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock) => (
                        <tr key={stock.symbol}>
                            <td>{stock.symbol}</td>
                            <td>{stock.price}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </>


    );
}

export default Dashboard;
